import React, { useState, useEffect } from 'react';
import { Button, Nav, Navbar as BootstrapNavbar } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
      <BootstrapNavbar expand='lg' className='navbar'>
        <BootstrapNavbar.Brand>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            TRVL
            <i className='fab fa-typo3' />
          </Link>
        </BootstrapNavbar.Brand>
        <BootstrapNavbar.Toggle aria-controls='basic-navbar-nav' onClick={handleClick} />
        <BootstrapNavbar.Collapse id='basic-navbar-nav'>
          <Nav className='ml-auto'>
            <Nav.Link as={Link} to='/' onClick={closeMobileMenu}>
              Home
            </Nav.Link>
            <Nav.Link as={Link} to='/services' onClick={closeMobileMenu}>
              Services
            </Nav.Link>
            <Nav.Link as={Link} to='/products' onClick={closeMobileMenu}>
              Products
            </Nav.Link>
            {button && (
              <Nav.Link as={Link} to='/sign-up' onClick={closeMobileMenu}>
                <Button buttonStyle='btn--outline'>SIGN UP</Button>
              </Nav.Link>
            )}
          </Nav>
        </BootstrapNavbar.Collapse>
      </BootstrapNavbar>
    </>
  );
}

export default Navbar;
